package com.example.controller;import com.example.dto.ApiResponse;import com.example.dto.JwtDTO;import com.example.dto.ArticleDTO;import com.example.entity.ProfileEntity;import com.example.service.ArticleService;import com.example.util.SecurityUtil;import jakarta.servlet.http.HttpServletRequest;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.domain.Page;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("")public class ArticleController {    @Autowired    ArticleService articleService;    @PostMapping("/moderator/create")    public ResponseEntity<ApiResponse> create(@RequestBody ArticleDTO articleDTO,                                              HttpServletRequest request) {        JwtDTO jwtDTO = SecurityUtil.hasRole(request, ProfileEntity.Role.MODERATOR);        return ResponseEntity                .status(HttpStatus.CREATED)                .body(articleService.create(articleDTO, jwtDTO.getId()));    }    @PutMapping("/moderator/update/{id}")    public ResponseEntity<ApiResponse> update(@PathVariable String id,                                             @RequestBody ArticleDTO articleDTO,                                             HttpServletRequest request) {        JwtDTO jwtDTO = SecurityUtil.hasRole(request, ProfileEntity.Role.MODERATOR);        return ResponseEntity.ok(articleService.update(id, articleDTO));    }    @PutMapping("/admin/update/{profileId}")    public ResponseEntity<ApiResponse> updateStatus(@PathVariable String articleId,                                                   HttpServletRequest request) {        JwtDTO jwtDTO = SecurityUtil.hasRole(request, ProfileEntity.Role.PUBLISHER);        return ResponseEntity.ok(articleService.updateStatus(articleId));    }    @DeleteMapping("/admin/delete/{id}")    public ResponseEntity<String> delete(@PathVariable Long id,                                         HttpServletRequest request) {        JwtDTO jwtDTO = SecurityUtil.hasRole(request, ProfileEntity.Role.ADMIN);        String response = articleService.delete(jwtDTO.getId(), id);        return ResponseEntity.ok(response);    }    @GetMapping("/admin/all")    public ResponseEntity<Page<ArticleDTO>> getAll(@RequestParam(defaultValue = "0") Integer page,                                                   @RequestParam(defaultValue = "5") Integer size,                                                   HttpServletRequest request) {        JwtDTO jwtDTO = SecurityUtil.hasRole(request, ProfileEntity.Role.ADMIN);        Page<ArticleDTO> articleDTOPage = articleService.getAll(page, size);        return ResponseEntity.ok(articleDTOPage);    }    @GetMapping("/{id}")    public ResponseEntity<?> getById(@PathVariable Long id) {        ArticleDTO articleDTO = articleService.getById(id);        return ResponseEntity.ok(articleDTO);    }}