package com.example.controller;import com.example.dto.ApiResponse;import com.example.dto.AuthDTO;import com.example.dto.ProfileDTO;import com.example.service.AuthService;import com.example.service.ProfileService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/api/v1/auth")public class AuthController {    @Autowired    AuthService authService;    @Autowired    ProfileService profileService;    @PostMapping(value = "/login")    public ResponseEntity<ApiResponse> login(@RequestBody AuthDTO authDTO) {        return ResponseEntity.ok(authService.login(authDTO));    }}