package com.example.controller;import com.example.dto.ApiResponse;import com.example.dto.AuthDTO;import com.example.dto.RegistrationDTO;import com.example.service.AuthService;import com.example.service.ProfileService;import com.example.util.HTMLUtil;import jakarta.servlet.http.HttpServletResponse;import jakarta.validation.Valid;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.io.IOException;@RestController@RequestMapping("/api/v1/auth")public class AuthController {    @Autowired    AuthService authService;    @Autowired    ProfileService profileService;    @PostMapping(value = "/login")    public ResponseEntity<ApiResponse> login(@RequestBody AuthDTO authDTO) {        return ResponseEntity.ok(authService.login(authDTO));    }    @PostMapping(value = "/registration")    public ResponseEntity<ApiResponse> registration(@Valid @RequestBody RegistrationDTO registrationDTO) {        return ResponseEntity.ok(authService.registration(registrationDTO));    }    @PostMapping(value = "/registration/by-email")    public ResponseEntity<ApiResponse> registrationByEmail(@Valid @RequestBody RegistrationDTO registrationDTO) {        return ResponseEntity.ok(authService.registrationByEmail(registrationDTO));    }    @PostMapping(value = "/registration/by-sms")    public ResponseEntity<ApiResponse> registrationBySms(@Valid @RequestBody RegistrationDTO registrationDTO) {        return ResponseEntity.ok(authService.registrationBySms(registrationDTO));    }}