package com.example.controller;import com.example.dto.CategoryDTO;import com.example.entity.CategoryEntity;import com.example.service.CategoryService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.domain.Page;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/category")public class CategoryController {    @Autowired    private CategoryService categoryService;    @PostMapping    public ResponseEntity<CategoryDTO> create(@RequestBody CategoryDTO categoryDTO) {        CategoryDTO createdCategory = categoryService.create(categoryDTO);        return ResponseEntity.status(HttpStatus.CREATED).body(createdCategory);    }    @PutMapping("/{id}")    public ResponseEntity<CategoryDTO> update(@PathVariable Integer id,                                              @RequestBody CategoryDTO categoryDTO) {        CategoryDTO updatedCategory = categoryService.update(id, categoryDTO);        return ResponseEntity.status(HttpStatus.CREATED).body(updatedCategory);    }    @DeleteMapping("/{id}")    public ResponseEntity<String> delete(@PathVariable Integer id) {        String resp = categoryService.delete(id);        return ResponseEntity.ok(resp);    }    @GetMapping("/{lang}")    public ResponseEntity<Page<CategoryDTO>> getByLang(@PathVariable CategoryEntity.Language lang,                                                       @RequestParam(defaultValue = "0") Integer page,                                                       @RequestParam(defaultValue = "5") Integer size) {        Page<CategoryDTO> categoryDTOPage = categoryService.getByLang(lang, page, size);        return ResponseEntity.ok(categoryDTOPage);    }}