package com.example.controller;import com.example.dto.ApiResponse;import com.example.dto.CommentDTO;import com.example.dto.JwtDTO;import com.example.service.CommentService;import com.example.util.SecurityUtil;import jakarta.servlet.http.HttpServletRequest;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/api/v1/comment")public class CommentController {    @Autowired    CommentService commentService;    @PostMapping    public ResponseEntity<ApiResponse> create(@RequestBody CommentDTO commentDTO,                                              HttpServletRequest request) {        JwtDTO jwtDTO = SecurityUtil.hasRole(request, null);        return ResponseEntity.ok(commentService.create(jwtDTO.getId(), commentDTO));    }    @PutMapping("/{id}")    public ResponseEntity<ApiResponse> update(@PathVariable String id,                                              @RequestBody CommentDTO commentDTO,                                              HttpServletRequest request) {        JwtDTO jwtDTO = SecurityUtil.hasRole(request, null);        return ResponseEntity.ok(commentService.update(id, commentDTO));    }    @DeleteMapping("/{id}")    public ResponseEntity<ApiResponse> delete(@PathVariable String id,                                              @RequestBody CommentDTO commentDTO,                                              HttpServletRequest request) {        JwtDTO jwtDTO = SecurityUtil.hasRole(request, null);        return ResponseEntity.ok(commentService.delete(id, commentDTO));    }}