package com.example.controller;import com.example.dto.ApiResponse;import com.example.dto.CommentLikeDTO;import com.example.dto.JwtDTO;import com.example.service.CommentLikeService;import com.example.util.SecurityUtil;import jakarta.servlet.http.HttpServletRequest;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/api/v1/comment-like")public class CommentLikeController {    @Autowired    private CommentLikeService commentLikeService;    @PostMapping("/close/create/like")    public ResponseEntity<ApiResponse> createLike(@RequestBody CommentLikeDTO commentLike,                                                  HttpServletRequest request) {        JwtDTO jwtDTO = SecurityUtil.hasRole(request, null);        ApiResponse apiResponse = commentLikeService.createLike(jwtDTO.getId(), commentLike);        return ResponseEntity.ok(apiResponse);    }    @PostMapping("/close/create/disLike")    public ResponseEntity<ApiResponse> createdisLike(@RequestBody CommentLikeDTO commentLike,                                                  HttpServletRequest request) {        JwtDTO jwtDTO = SecurityUtil.hasRole(request, null);        ApiResponse apiResponse = commentLikeService.createdDisLike(jwtDTO.getId(), commentLike);        return ResponseEntity.ok(apiResponse);    }}