package com.example.controller;import com.example.dto.ApiResponse;import com.example.dto.CommentLikeDTO;import com.example.service.CommentLikeService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/api/v1/comment-like")public class CommentLikeController {    @Autowired    private CommentLikeService commentLikeService;    @PostMapping("/open/create/like")    public ResponseEntity<ApiResponse> createLike(@RequestBody CommentLikeDTO commentLike) {        ApiResponse apiResponse = commentLikeService.createLike(commentLike);        return ResponseEntity.ok(apiResponse);    }    @PostMapping("/open/create/disLike")    public ResponseEntity<ApiResponse> createdLike(@RequestBody CommentLikeDTO commentLike) {        ApiResponse apiResponse = commentLikeService.createdLike(commentLike);        return ResponseEntity.ok(apiResponse);    }}