package com.example.controller;import com.example.dto.ProfileDTO;import com.example.service.ProfileService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.domain.Page;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/api/v1/profile")public class ProfileController {    @Autowired    ProfileService profileService;    @PostMapping    public ResponseEntity<ProfileDTO> create(@RequestBody ProfileDTO profileDTO) {        ProfileDTO createdProfile = profileService.create(profileDTO);        return ResponseEntity.status(HttpStatus.CREATED).body(createdProfile);    }    @PutMapping("/{id}")    public ResponseEntity<ProfileDTO> update(@PathVariable Long id,                                             @RequestBody ProfileDTO profileDTO) {        ProfileDTO updatedProfile = profileService.update(id, profileDTO);        return ResponseEntity.ok(updatedProfile);    }    @DeleteMapping("/{id}")    public ResponseEntity<String> update(@PathVariable Long id) {        String response = profileService.delete(id);        return ResponseEntity.ok(response);    }    @GetMapping()    public ResponseEntity<Page<ProfileDTO>> getList(@RequestParam(defaultValue = "0") Integer page,                                                    @RequestParam(defaultValue = "5") Integer size) {        Page<ProfileDTO> profileDTOPage = profileService.getList(page, size);        return ResponseEntity.ok(profileDTOPage);    }    @GetMapping("/{id}")    public ResponseEntity<?> getById(@PathVariable Long id) {        ProfileDTO profileDTO = profileService.getById(id);        return ResponseEntity.ok(profileDTO);    }}