package com.example.controller;import com.example.dto.JwtDTO;import com.example.dto.RegionDTO;import com.example.entity.ProfileEntity;import com.example.entity.RegionEntity;import com.example.service.RegionService;import com.example.util.SecurityUtil;import jakarta.servlet.http.HttpServletRequest;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.domain.Page;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/api/v1/region")public class RegionController {    @Autowired    RegionService regionService;    @PostMapping("/admin/create")    public ResponseEntity<?> create(@RequestBody RegionDTO regionDTO,                                    HttpServletRequest request) {        JwtDTO jwtDTO = SecurityUtil.hasRole(request, ProfileEntity.Role.ADMIN);        RegionDTO createdRegionDTO = regionService.create(regionDTO, jwtDTO.getId());        return ResponseEntity.status(HttpStatus.CREATED).body(createdRegionDTO);    }    @PutMapping("/admin/update/{id}")    public ResponseEntity<?> update(@PathVariable Long id,                                    @RequestBody RegionDTO regionDTO,                                    HttpServletRequest request) {        JwtDTO jwtDTO = SecurityUtil.hasRole(request, ProfileEntity.Role.ADMIN);        Boolean resp = regionService.update(id, regionDTO);        return ResponseEntity.ok(resp);    }    @GetMapping("/admin/all")    public ResponseEntity<?> getList(@RequestParam(defaultValue = "0") Integer page,                                     @RequestParam(defaultValue = "5") Integer size) {        Page<RegionDTO> regionDTOPage = regionService.getList(page, size);        return ResponseEntity.ok(regionDTOPage);    }    @DeleteMapping("/admin/delete/{id}")    public ResponseEntity<?> deleteById(@PathVariable Long id) {        regionService.deleteById(id);        return ResponseEntity.noContent().build();    }    @GetMapping("/admin/lang")    public ResponseEntity<?> getByLang(@RequestParam(defaultValue = "uz") RegionEntity.Language lang,                                       @RequestParam(defaultValue = "0") Integer page,                                       @RequestParam(defaultValue = "5") Integer size) {        Page<RegionDTO> regionDTOPage = regionService.getByLang(lang, page, size);        return ResponseEntity.ok(regionDTOPage);    }}