package com.example.dto;import jakarta.validation.constraints.NotNull;import jakarta.validation.constraints.Pattern;import lombok.AllArgsConstructor;import lombok.Data;@Data@AllArgsConstructorpublic class CustomerDTO {    @Pattern(regexp = "^\\+?[1-9]\\d{7,14}$", message = "Invalid phone number format")    private String phone;    @NotNull(message = "message is required")    private String message;    private String text;}