package com.example.dto;import com.example.entity.ProfileEntity;import com.fasterxml.jackson.annotation.JsonInclude;import lombok.AllArgsConstructor;import lombok.Data;@Data@AllArgsConstructor@JsonInclude(JsonInclude.Include.NON_NULL)public class JwtDTO {    private Long id;    private String phone;    private ProfileEntity.Role role;    public JwtDTO(Long id, ProfileEntity.Role role) {        this.id = id;        this.role = role;    }    public JwtDTO(String phone, ProfileEntity.Role role) {        this.phone = phone;        this.role = role;    }}