package com.example.dto;import com.fasterxml.jackson.annotation.JsonInclude;import jakarta.validation.constraints.Email;import jakarta.validation.constraints.NotNull;import jakarta.validation.constraints.Pattern;import jakarta.validation.constraints.Size;import lombok.Data;@Data@JsonInclude(JsonInclude.Include.NON_NULL)public class RegistrationDTO {    @NotNull(message = "Name is required")    @Size(min = 3, message = "Name should be at least 3 characters")    private String name;    private String surname;    @Email(message = "Invalid email format")    @NotNull(message = "email is null!")    private String email;    @NotNull(message = "phone is null!")    @Pattern(regexp = "^\\+?[1-9]\\d{7,14}$", message = "Invalid phone number format")    private String phone;    @NotNull(message = "Password is required")    @Size(min = 6, message = "Password should be at least 6 characters")    private String password;}