package com.example.entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import java.util.UUID;@Entity@Table(name = "article_type")@Data@AllArgsConstructor@NoArgsConstructorpublic class ArticleTypeEntity {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private UUID id = UUID.randomUUID();    @Column(name = "order_number")    private Integer orderNumber;    @Column(name = "name_uz",            nullable = false)    private String nameUz;    @Column(name = "name_ru",            nullable = false)    private String nameRU;    @Column(name = "name_en",            nullable = false)    private String nameEN;    @Column(name = "visibility",            nullable = false)    private Boolean visibility;}