package com.example.entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@Entity@Table(name = "attach")@Data@NoArgsConstructor@AllArgsConstructorpublic class Attach {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(name = "file_name",            nullable = false)    private String fileName;    @Column(name = "file_type",            nullable = false)    private Type fileType;    @Lob    @Column(name = "file_data",            nullable = false)    private Byte[] fileData;    public enum Type {        IMAGE,        VIDEO    }}