package com.example.entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import java.time.LocalDateTime;import java.util.UUID;@Entity@Table(name = "attach")@Data@NoArgsConstructor@AllArgsConstructorpublic class AttachEntity {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private UUID id = UUID.randomUUID();    @Column(name = "file_name",            nullable = false)    private String fileName;    @Column(name = "file_path",            nullable = false)    private String filePath;    @Column(name = "file_size",            nullable = false)    private Double fileSize;    @Column(name = "file_type",            nullable = false)    private Type fileType;    @Lob    @Column(name = "file_data"/*,            nullable = false*/)    private Byte[] fileData;    @Column(name = "created_date")    private LocalDateTime createdDate = LocalDateTime.now();    public enum Type {        IMAGE,        VIDEO,        AUDIO,        FILE    }}