package com.example.entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import java.time.LocalDateTime;import java.util.UUID;@Entity@Table(name = "comment")@Data@NoArgsConstructor@AllArgsConstructorpublic class CommentEntity {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private UUID id = UUID.randomUUID();    @Column(name = "created_date")    private LocalDateTime createdDate = LocalDateTime.now();    @Column(name = "updated_date")    private LocalDateTime updatedDate;    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "profile_id",            nullable = false)    private ProfileEntity profileEntity;    @Column(name = "content",            columnDefinition = "TEXT")    private String content;    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "article_id",            nullable = false)    private ArticleEntity articleEntity;    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "reply_article_id")    private ArticleEntity replyArticleEntity;    @Column(name = "visibility",            nullable = false)    private Boolean visibility;}