package com.example.entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import java.time.LocalDateTime;import java.util.UUID;@Entity@Table(name = "comment_like")@Data@NoArgsConstructor@AllArgsConstructorpublic class CommentLikeEntity {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private UUID id = UUID.randomUUID();    @Column(name = "created_date")    private LocalDateTime createdDate = LocalDateTime.now();    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "profile_id",            nullable = false)    private ProfileEntity profileEntity;    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "comment_id",            nullable = false)    private CommentEntity commentEntity;    @Enumerated(EnumType.STRING)    private ProfileEntity.Status status;}