package com.example.entity;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.Table;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@Data@AllArgsConstructor@NoArgsConstructor@Entity@Table(name = "email_history")public class EmailHistoryEntity extends BaseStringEntity {    @Column(name = "message", columnDefinition = "TEXT")    private String message;    @Column(name = "email")    private String email;}