package com.example.entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import java.time.LocalDateTime;@Entity@Table(name = "email_history")@Data@NoArgsConstructor@AllArgsConstructorpublic class EmailHistoryEntity {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(name = "message",    columnDefinition = "text")    private String message;    @Column(name = "email")    private String email;    @Column(name = "created_date")    private LocalDateTime createdDate;}