package com.example.entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import java.time.LocalDateTime;@Entity@Table(name = "profile")@Data@AllArgsConstructor@NoArgsConstructorpublic class ProfileEntity extends BaseEntity {    @Column(name = "name")    private String name;    @Column(name = "surname")    private String surname;    @Column(name = "email",            unique = true)    private String email;    @Column(name = "phone",            unique = true,            length = 13)    private String phone;    @Column(name = "password")    private String password;    @Column(name = "status")    @Enumerated(EnumType.STRING)    private Status status;    @Column(name = "role")    @Enumerated(EnumType.STRING)    private Role role;    @OneToOne(fetch = FetchType.EAGER)    @JoinColumn(name = "image_id")    private AttachEntity image;    @Column(name = "prtId")    private Long prtId;    public enum Status {        ACTIVE, INACTIVE    }    public enum Role {        ADMIN, USER, PUBLISHER, MODERATOR    }}