package com.example.repository;import com.example.entity.CommentEntity;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.repository.JpaRepository;import java.time.LocalDateTime;public interface CommentRepository extends JpaRepository<CommentEntity, String> {    Page<CommentEntity> findAllByArticleId(String id, Pageable pageable);    Page<CommentEntity> findAllByIdAndArticleId(String id, String articleId, Pageable pageable);    Page<CommentEntity> findAllByArticleIdAndCreatedDateBetween(String articleId, LocalDateTime startingDate, LocalDateTime endingDate, Pageable pageable);    Page<CommentEntity> findAllByArticleIdAndUpdatedDateBetween(String articleId, LocalDateTime startingDate, LocalDateTime endingDate, Pageable pageable);    Page<CommentEntity> findAllByProfileIdAndArticleId(Long profileId, String articleId, Pageable pageable);    Page<CommentEntity> findAllByProfileEntityNameAndArticleId(String name, String articleId, Pageable pageable);    Page<CommentEntity> findAllByProfileEntitySurnameAndArticleId(String surname, String articleId, Pageable pageable);    Page<CommentEntity> getCommentPageById(String id, Pageable pageable);    Page<CommentEntity> findAllByCreatedDateBetween(LocalDateTime startingDate, LocalDateTime endingDate, Pageable pageable);    Page<CommentEntity> findAllByUpdatedDateBetween(LocalDateTime startingDate, LocalDateTime endingDate, Pageable pageable);    Page<CommentEntity> findAllByProfileId(Long profileId, Pageable pageable);    Page<CommentEntity> findAllByProfileEntityName(String name, Pageable pageable);    Page<CommentEntity> findAllByProfileEntitySurname(String surname, Pageable pageable);}