package com.example.repository;import com.example.entity.EmailHistoryEntity;import org.springframework.data.jpa.repository.JpaRepository;import java.time.LocalDateTime;import java.util.List;public interface EmailHistoryRepository extends JpaRepository<EmailHistoryEntity, String> {    boolean existsByEmail(String email);    void deleteByEmail(String email);    List<EmailHistoryEntity> findAllByEmail(String email);    List<EmailHistoryEntity> findAllByMessage(String message);    List<EmailHistoryEntity> findAllByCreatedDateBetween(LocalDateTime startingDate, LocalDateTime endingDate);}