package com.example.security;import jakarta.servlet.Filter;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.web.servlet.FilterRegistrationBean;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class SecurityFilterConfig {    @Autowired    private JWTFilter jwtFilter;    @Bean    public FilterRegistrationBean filterRegistrationBean() {        FilterRegistrationBean<Filter> bean = new FilterRegistrationBean<>();        bean.setFilter(jwtFilter);        bean.addUrlPatterns("/api/v1/profile/admin/*");        bean.addUrlPatterns("/api/v1/profile/moderator/*");        bean.addUrlPatterns("/api/v1/article-like/close/*");        bean.addUrlPatterns("/api/v1/category/admin/*");        bean.addUrlPatterns("/api/v1/comment/close/*");        bean.addUrlPatterns("/api/v1/comment/admin/*");        bean.addUrlPatterns("/api/v1/comment-like/close/*");        bean.addUrlPatterns("/api/v1/email-history/admin/*");        bean.addUrlPatterns("/api/v1/region/admin/*");        bean.addUrlPatterns("/api/v1/article/moderator/*");        bean.addUrlPatterns("/api/v1/article/admin/*");        return bean;    }}