package com.example.util;import lombok.experimental.UtilityClass;@UtilityClasspublic class HTMLUtil {    public String getSuccessRegistration() {        return "<!DOCTYPE html>\n" +                "<html>\n" +                "<head>\n" +                "  <title>Registration Completed</title>\n" +                "  <style>\n" +                "    body {\n" +                "      display: flex;\n" +                "      justify-content: center;\n" +                "      align-items: center;\n" +                "      height: 100vh;\n" +                "      margin: 0;\n" +                "      background-color: #f2f2f2;\n" +                "    }\n" +                "\n" +                "    .message {\n" +                "      font-size: 36px;\n" +                "      font-weight: bold;\n" +                "      color: #007BFF;\n" +                "      animation: fadeIn 2s ease-in-out;\n" +                "      transition: background-color 0.3s;\n" +                "    }\n" +                "\n" +                "    .message:hover {\n" +                "      background-color: #FFC107;\n" +                "    }\n" +                "\n" +                "    @keyframes fadeIn {\n" +                "      from {\n" +                "        opacity: 0;\n" +                "      }\n" +                "      to {\n" +                "        opacity: 1;\n" +                "      }\n" +                "    }\n" +                "\n" +                "    </style>\n" +                "    </head>\n" +                "    <body>\n" +                "    <p class=\"message\">Congratulations! Registration completed successfully!</p>\n" +                "    </body>\n" +                "    </html>";    }    public static String getRegistrationButton(String url) {        return "<!DOCTYPE html>\n" +                "<html>\n" +                "<head>\n" +                "  <title>Beautiful Button</title>\n" +                "  <style>\n" +                "    body {\n" +                "      display: flex;\n" +                "      justify-content: center;\n" +                "      align-items: center;\n" +                "      height: 100vh;\n" +                "      margin: 0;\n" +                "      text-align: center;" +                "    }\n" +                "\n" +                "    .button {\n" +                "      display: inline-block;\n" +                "      padding: 15px 30px;\n" +                "      font-size: 18px;\n" +                "      font-weight: bold;\n" +                "      text-align: center;\n" +                "      text-decoration: none;\n" +                "      background-image: linear-gradient(to right, #4CAF50, #45a049);\n" +                "      color: #ffffff;\n" +                "      border-radius: 30px;\n" +                "      box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);\n" +                "      transition: transform 0.3s, box-shadow 0.3s, background-image 0.3s;\n" +                "    }\n" +                "\n" +                "    .button:hover {\n" +                "      transform: translateY(-2px);\n" +                "      box-shadow: 0px 6px 12px rgba(0, 0, 0, 0.2);\n" +                "      background-image: linear-gradient(to right, #45a049, #4CAF50);\n" +                "    }\n" +                "\n" +                "    .button:active {\n" +                "      transform: translateY(0);\n" +                "      box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);\n" +                "    }\n" +                "  </style>\n" +                "</head>\n" +                "<body>\n" +                String.format("<a href=\"%s\" class=\"button\">Click link to complete registration </a>\n", url) +                "</body>\n" +                "</html>\n";    }}