package com.example.util;import com.example.config.CustomUserDetails;import lombok.experimental.UtilityClass;import org.springframework.security.core.Authentication;import org.springframework.security.core.context.SecurityContextHolder;@UtilityClasspublic class SpringSecurityUtil {    public String getCurrentUsername() {        Authentication authentication = SecurityContextHolder.getContext().getAuthentication();        return authentication.getName();    }    public CustomUserDetails getCurrentUser() {        Authentication authentication = SecurityContextHolder.getContext().getAuthentication();        return (CustomUserDetails) authentication.getPrincipal();    }}